	//Serializing a JSON string to an object 
	public static <T> T getObject(String JSONString, Class<T> o){
        //String JSONString = null;
        ObjectMapper mapper = new ObjectMapper(); 
        T t = null;
		

        try {
        	if(JSONString != null){
        		t = mapper.readValue(JSONString, o);
        	}
        } catch (JsonParseException e1) {
        	//Log.i(TAG, "EXCEPTION: Caught a JsonParseException in getObject");
			e1.printStackTrace();
			return t;
		} catch (JsonMappingException e1) {
			//Log.i(TAG, "EXCEPTION: Caught a JsonMappingException in getObject");
			e1.printStackTrace();
			return t;
		} catch (IOException e1) {
			//Log.i(TAG, "EXCEPTION: Caught a IOException in getObject");
			e1.printStackTrace();
			return t;
		}
		
		return t;
	}
	
	public static <T> T getMultipleObjects(String JSONString, TypeReference<T> o){
        T t = null;
        ObjectMapper mapper = new ObjectMapper();
      
        try {
        	if(JSONString != null){
        		t = mapper.readValue(JSONString, o);
        	}
        } catch (JsonParseException e1) {
        	//Log.i(TAG, "EXCEPTION: Caught a JsonParseException in getMultipleObject");
        	e1.printStackTrace();
			return t;
		} catch (JsonMappingException e1) {
			//Log.i(TAG, "EXCEPTION: Caught a JsonMappingException in getMultipleObject");
			e1.printStackTrace();
			return t;
		} catch (IOException e1) {
			//Log.i(TAG, "EXCEPTION: Caught a IOException in getMultipleObject");
			e1.printStackTrace();
			return t;
		}		
		return t;
	}
